workspace.query:
xquery version "1.0-ml";

for $each in collection("examples")
return fn:document-uri($each)

xquery version "1.0-ml";

declare function local:print-word ($word as xs:string) { fn:upper-case($word) };

local:print-word( ("hello", "world") )

import module namespace sec="http://marklogic.com/xdmp/security" at "/MarkLogic/security.xqy";
declare function local:dump-perms($uri)
{
  for $perm in xdmp:document-get-permissions($uri)
  let $role-name :=
    xdmp:invoke-function(
      function() {
        try {
          sec:get-role-names($perm/sec:role-id)
        }
        catch($ex) {("could not get perms")}   
      }, 
      <options xmlns="xdmp:eval">
        <database>{xdmp:security-database()}</database>
      </options>
    )
  return
    <role id="{$perm/sec:role-id}" name="{$role-name}" capability="{$perm/sec:capability}"></role>
};
local:dump-perms("/documents/ewhc_ch_2004_324.xml")

(: Iterate through all documents in the "examples" collection and construct a new summary XML document. :)
declare namespace akn="http://docs.oasis-open.org/legaldocml/ns/akn/3.0"; declare namespace uk="https://caselaw.nationalarchives.gov.uk/akn";
<summaries> { for $doc in collection("examples") let $meta := $doc/akn:akomaNtoso/akn:judgment/akn:meta let $uri := fn:document-uri($doc) let $name := $meta/akn:identification/akn:FRBRWork/akn:FRBRname/@value let $date := $meta/akn:identification/akn:FRBRWork/akn:FRBRdate[@name='judgment']/@date let $court := $meta/akn:proprietary/uk:court/text() let $citation := $meta/akn:proprietary/uk:cite/text() where $date>"2020-01-02" order by $name descending return <summary> <uri>{$uri}</uri> <name>{$name}</name> <judgmentDate>{$date}</judgmentDate> <court>{$court}</court> <citation>{$citation}</citation> </summary> } </summaries>

xquery version "1.0-ml";
declare namespace akn="http://docs.oasis-open.org/legaldocml/ns/akn/3.0"; declare namespace tna="https://caselaw.nationalarchives.gov.uk/akn";
(: parameters :) declare variable $sort_by as xs:string external := "name"; declare variable $sort_direction as xs:string external := "asc";
(: construct summary data :) let $summaries := for $doc in collection("examples") let $meta := $doc/akn:akomaNtoso/akn:judgment/akn:meta return <summary> <uri>{fn:document-uri($doc)}</uri> <name>{$meta/akn:identification/akn:FRBRWork/akn:FRBRname/@value/string()}</name> <judgmentDate>{$meta/akn:identification/akn:FRBRWork/akn:FRBRdate[@name='judgment']/@date/string()}</judgmentDate> <court>{$meta/akn:proprietary/tna:court/text()}</court> <citation>{$meta/akn:proprietary/tna:cite/text()}</citation> </summary>
(: sort summaries by default ascending :) let $sorted_summaries := for $summary in $summaries order by (: sort key switch :) switch ($sort_by) case "name" return $summary/name case "date" return xs:date($summary/judgmentDate) (: Cast to xs:date for correct sorting :) case "court" return $summary/court case "citation" return $summary/citation default return xs:date($summary/judgmentDate) ascending return $summary
(: reverse ordering if required :) let $final_summaries := if ($sort_direction = "desc") then fn:reverse($sorted_summaries) else $sorted_summaries
(: wrap summaries in "summaries" root element :) return <summaries> {$final_summaries} </summaries>
